import React, { Component } from 'react';
import ApiService from '../../ApiService';

class EditUserComponent extends  Component {

    constructor(props) {
        super(props);
         this.state = {
             username: '',
             password: '',
             firstName: '',
             lastName: '',
             age: '',
             salary: '',
             message: null
         }
    }

    componentDidMount() {
        this.loadUser();
    }

    loadUser = () => {
        ApiService.fecthUserByID(window.localStorage.getItem("userID"))
        .then( res => {
            let user = res.data;
            this.setState({
                id: user.id,
                username: user.username,
                firstName: user.firstName,
                lastName: user.lastName,
                age: user.age,
                salary: user.salary
            })
        })
        .catch(err => {
            console.log('loadUser() err', err);
        })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    saveUser = (e) => {
        e.preventDefault();

        let user = {
            username: this.state.username,
            password: this.state.password,
            firstName: this.state.firstaName,
            lastName: this.state.lastName,
            age: this.state.age,
            salary: this.state.salary
        }

        ApiService.editUser(user)
        .then( res => {
            this.setState({
                message: user.username + "updated successfully."
            })
            console.log(this.state.message);
            this.props.history.push('/users');
        })
        .catch ( err => {
            console.log('saveUser() err', err);
        });
    }

    render() {
        return(
            <div>
                <h2>Edit User</h2>
                <form>
                    <div>
                        <label>User Name:</label>
                        <input type="text" placeholder="username" name="username"
                         value={this.state.username} onChange={this.onChange}></input>
                    </div>

                    <div>
                        <label>Password:</label>
                        <input type="password" placeholder="password" name="password"
                         value={this.state.password} onChange={this.onChange}></input>
                    </div>

                    <div>
                        <label>First Name:</label>
                        <input type="text" placeholder="first name" name="firstName"
                         value={this.state.firstaName} onChange={this.onChange}></input>
                    </div>

                    <div>
                        <label>Last Name:</label>
                        <input type="text" placeholder="last name" name="lastName"
                         value={this.state.lastName} onChange={this.onChange}></input>
                    </div>

                    <div>
                        <label>Age:</label>
                        <input type="number" placeholder="age" name="age"
                         value={this.state.age} onChange={this.onChange}></input>
                    </div>

                    <div>
                        <label>Salary:</label>
                        <input type="number" placeholder="salary" name="salary"
                         value={this.state.salary} onChange={this.onChange}></input>
                    </div>

                    <button onClick={this.saveUser}>Save</button>
                </form>
            </div>
        );
    }
}

export default EditUserComponent;
